

import typing

import jk_prettyprintobj

from .htmlgeneral import *
from .HTMLText import HTMLText






class _HTMLTextProto(jk_prettyprintobj.DumpMixin):

	################################################################################################################################
	## Constructor
	################################################################################################################################

	#
	# Constructor method.
	#
	def __init__(self, implClass=HTMLText):
		self.implClass = implClass
	#

	################################################################################################################################
	## Public Properties
	################################################################################################################################

	################################################################################################################################
	## Helper Methods
	################################################################################################################################

	def _dumpVarNames(self) -> typing.List[str]:
		return [
			"implClass",
		]
	#

	################################################################################################################################
	## Public Methods
	################################################################################################################################

	def __call__(self, *args, **attrs):
		return self.implClass(args)()
	#

	def __getitem__(self, children):
		return self.implClass("".join(children))
	#

#











